export interface Profile {
  email: string,
  groups: string[],
  issuedAt: number,
  expiresAt: number,
  sourceIp: string,
}

export interface Fundraiser {
  id: string,
  fundraiserName: string,
  activeFrom: number,
  activeTo: number,
  paused: boolean,
  goal: number,
  totalRaised: number,
  donationsCount: number,
  matchFundingRate: number,
  matchFundingPerDonationLimit: number | null,
  matchFundingRemaining: number | null,
  minimumDonationAmount: number | null,
  suggestedDonationAmountOneOff: number,
  suggestedDonationAmountWeekly: number,
  suggestedContributionAmount: number | null,
  groupsWithAccess: string[],
}

export interface Donation {
  donorName: string,
  donorEmail: string,
  emailConsentInformational: boolean,
  emailConsentMarketing: boolean,
  createdAt: number,
  addressLine1: string | null,
  addressLine2: string | null,
  addressLine3: string | null,
  addressPostcode: string | null,
  addressCountry: string | null,
  giftAid: boolean,
  comment: string | null,
  donationAmount: number,
  matchFundingAmount: number,
  contributionAmount: number,
  recurringAmount: number | null,
  recurrenceFrequency: ("WEEKLY" | "MONTHLY") | null,
  stripeCustomerId: string | null,
  stripePaymentMethodId: string | null,
  charity: string,
  overallPublic: boolean,
  namePublic: boolean,
  donationAmountPublic: boolean,
  id: string,
  fundraiserId: string,
}

export interface Payment {
  at: number,
  donationAmount: number,
  contributionAmount: number,
  matchFundingAmount: number | null,
  method: "card" | "cash" | "direct_to_charity",
  reference: string | null,
  status: "paid" | "pending" | "cancelled" | "refunded",
  id: string,
  donationId: string,
  fundraiserId: string,
}

export interface PublicFundraiser {
  id: string,
  activeFrom: number,
  activeTo: number | null,
  paused: boolean,
  goal: number,
  totalRaised: number,
  donationsCount: number,
  matchFundingRate: number,
  matchFundingPerDonationLimit: number | null,
  matchFundingRemaining: number | null,
  minimumDonationAmount: number | null,
  suggestedDonationAmountOneOff: number,
  suggestedDonationAmountWeekly: number,
  suggestedContributionAmount: number | null,
  donations: {
    donorName?: string,
    createdAt: number,
    giftAid?: boolean,
    comment: string | null,
    donationAmount?: number,
    matchFundingAmount?: number,
    recurringAmount?: number | null,
    recurrenceFrequency?: ("WEEKLY" | "MONTHLY") | null,
  }[],
}

export interface PublicDonationRequest {
  donationAmount: number,
  recurrenceFrequency: ("WEEKLY" | "MONTHLY") | null,
  contributionAmount: number,
  giftAid: boolean,
  donorName: string,
  donorEmail: string,
  emailConsentInformational: boolean,
  emailConsentMarketing: boolean,
  addressLine1: string | null,
  addressLine2: string | null,
  addressLine3: string | null,
  addressPostcode: string | null,
  addressCountry: string | null,
  overallPublic: boolean,
  namePublic: boolean,
  donationAmountPublic: boolean,
  comment: string | null,
}

export interface PublicPaymentIntentResponse {
  stripeClientSecret: string,
  amount: number,
  futurePayments: {
    amount: number,
    at: number,
  }[],
}
